// Lấy text đầu vào từ item
const input = $json.message.text;

// Hàm tách từ khóa ở cuối
function tachKyTuCuoi(text) {
  const pattern = /\b(a[1-3]|k|ok)\b$/i;
  const match = text.match(pattern);

  if (match) {
    const keyword = match[0];
    const content = text.slice(0, text.length - keyword.length).trim();
    return { content, keyword };
  } else {
    return { content: text, keyword: null };
  }
}

// Gọi hàm
const result = tachKyTuCuoi(input);

// Trả kết quả đúng định dạng cho n8n
return [
  {
    json: result
  }
];

