// Phân tích ID để tạo link dạng: https://www.facebook.com/{pageId}/posts/{postIdShort}
const [pageId, postIdShort] = post.id.split("_");

// Chuyển về đối tượng Date và cộng thêm 7 tiếng để thành giờ VN
const createdAtUTC = new Date(post.created_time);
const vietnamTime = new Date(createdAtUTC.getTime() + 7 * 60 * 60 * 1000);

// Format lại thành "YYYY-MM-DD HH:mm"
const pad = n => n.toString().padStart(2, '0');
const formattedDate = `${vietnamTime.getFullYear()}-${pad(vietnamTime.getMonth() + 1)}-${pad(vietnamTime.getDate())} ${pad(vietnamTime.getHours())}:${pad(vietnamTime.getMinutes())}`;

// Trả về kết quả
return [
  {
    json: {
      message: post.message,
      created_time_vietnam: formattedDate,
      original_time_utc: post.created_time,
      post_url: `https://facebook.com/${post.id}`,
      post_link: `https://www.facebook.com/${pageId}/posts/${postIdShort}`
    }
  }
];

